import React, { useState, useEffect, useCallback } from "react";

import TodoList from "./components/TodoList";
import AddTodo from "./components/AddTodo";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  // 화면 불러오고 있는 중에 로딩 표시 처리
  const [isLoading, setIsLoading] = useState(false);
  //에러상태 처리
  const [error, setError] = useState(null);

  //------useCallback을 사용한 경우
  const fetchMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);

    // try {} catch{}로 오류 처리 해주기
    // GET요청 관련 fetch
    try {
      const response = await fetch(
        "https://udemy-react-http-77f19-default-rtdb.firebaseio.com/movies.json"
      ); // firebase 링크끝에 /movies.json이라고 추가함
      if (!response.ok) {
        throw new Error("Something went wrong");
      }
      const data = await response.json();

      const loadedMovies = [];

      for (const key in data) {
        loadedMovies.push({
          id: key,
          title: data[key].title,
          openingText: data[key].openingText,
          releaseDate: data[key].releaseDate,
        });
      }
      setMovies(loadedMovies);
    } catch (error) {
      setError(error.message); // "Something went wrong"
    }
    setIsLoading(false);
  }, []);

  // 최초 로딩 시 영화 리스트 보이도록 함
  // 의존성 배열에 [fetchMoviesHandler]을 넣어주면 변화가 있을때마다 업데이트가 될수 있는 반면 infinite loop를 만들게 될수도 있다.
  // 이를 방지하기 위해 useCallback을 사용한다
  useEffect(() => {
    fetchMoviesHandler();
  }, [fetchMoviesHandler]);

  // POST 요청 관련 fetch
  async function addMovieHandler(movie) {
    const response = await fetch(
      "https://udemy-react-http-77f19-default-rtdb.firebaseio.com/movies.json",
      {
        method: "POST",
        body: JSON.stringify(movie),
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
    const data = await response.json();
    console.log(data); // 결과값: {name: '-N9a7yhrMvCi1GK_E4-1'} // firebase에서 정한 아이디
  }

  let content = <p>Found no movies</p>;
  if (movies.length > 0) {
    content = <TodoList movies={movies} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <React.Fragment>
      <section>
        <AddMovie onAddMovie={addMovieHandler} />
      </section>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>{content}</section>
    </React.Fragment>
  );
}

export default App;
