import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

// 버전 1  - 더 간단한 표현
function App() {
  const [movies, setMovies] = useState([]);
  // 화면 불러오고 있는 중에 로딩 표시 처리
  const [isLoading, setIsLoading] = useState(false);
  //에러상태 처리
  const [error, setError] = useState(null);

  async function fetchMoviesHandler() {
    setIsLoading(true);
    setError(null);

    // try {} catch{}로 오류 처리 해주기
    try {
      const response = await fetch("https://swapi.dev/api/film");
      if (!response.ok) {
        throw new Error("Something went wrong");
      }
      const data = await response.json();

      const transformedMovies = data.results.map((movieData) => {
        return {
          id: movieData.episode_id,
          title: movieData.title,
          openingText: movieData.opening_crawl,
          releaseDate: movieData.release_date,
        };
      });
      setMovies(transformedMovies);
    } catch (error) {
      setError(error.message); // "Something went wrong"
    }
    setIsLoading(false);
  }

  // 버전 2
  // function fetchMoviesHandler() {
  //   // fetch('url', {method:GET}) 두번째 인자의 디폴트 값이 GET이므로 여기서는 굳이 쓸 필요가 없음
  //   fetch("https://swapi.dev/api/films")
  //     .then((response) => {
  //       return response.json();
  //     })
  //     .then((data) => {
  //       //https://swapi.dev/api/films 여기서 제공하는 프로퍼티명이랑 Movie.js에서 props뒤에 쓴 형식이 다름. fetch로 받아올때 Movie.js에서 쓰인 형식으로 바꿔서 받아오는 처리 해주기!
  //       const transformedMovies = data.results.map((movieData) => {
  //         return {
  //           id: movieData.episode_id,
  //           title: movieData.title,
  //           openingText: movieData.opening_crawl,
  //           releaseDate: movieData.release_date,
  //         };
  //       });
  //       setMovies(transformedMovies);
  //     });
  // }

  // const dummyMovies = [
  //   {
  //     id: 1,
  //     title: 'Some Dummy Movie',
  //     openingText: 'This is the opening text of the movie',
  //     releaseDate: '2021-05-18',
  //   },
  //   {
  //     id: 2,
  //     title: 'Some Dummy Movie 2',
  //     openingText: 'This is the second opening text of the movie',
  //     releaseDate: '2021-05-19',
  //   },
  // ];

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && movies.length > 0 && <MoviesList movies={movies} />}
        {!isLoading && movies.length === 0 && <h2>Found no movies</h2>}
        {!isLoading && error && <p>{error}</p>}
        {isLoading && <h2>Loading...</h2>}
      </section>
    </React.Fragment>
  );
}

export default App;
